<template>
  <view
    :style="{ 'line-height': size + 'px' }"
    class="icon-wrapper"
    @click="_onClick"
  >
    <text
      v-if="custom"
      :style="{ 'font-size': size + 'px' }"
      class="iconfont"
      :class="[iconStyle]"
    >{{icons[type]}}</text>
    <text
      v-else
      :style="{ color: color, 'font-size': size + 'px' }"
      class="iconfont"
      :class="[iconStyle]"
    >{{icons[type]}}</text>
  </view>
</template>

<script>
import icons from './icons.js';

// #ifdef APP-NVUE
var domModule = weex.requireModule('dom');
domModule.addRule('fontFace', {
  "fontFamily": "iconfont",
  "src":        "url('data:font/truetype;charset=utf-8;base64,')",
});
// #endif

/**
 * Icons 图标
 * @description 用于展示 icons 图标
 * @property {Number} size 图标大小
 * @property {String} type 图标图案
 * @property {String} color 图标颜色
 * @event {Function} click 点击 Icon 触发事件
 */
export default {
  name:    'cpm-icons',
  props:   {
    type:      {
      type:    String,
      default: '',
    },
    color:     {
      type:    String,
      default: '#353535',
    },
    size:      {
      type:    [Number, String],
      default: 16,
    },
    custom:    {
      type:    Boolean,
      default: false,
    },
    iconStyle: {
      type:    String,
      default: '',
    },
  },
  data() {
    return {
      icons: icons,
    }
  },
  methods: {
    _onClick() {
      this.$emit('click')
    },
  },
}
</script>

<style scoped>
/* #ifndef APP-NVUE */
@font-face {
  font-family: iconfont;
  src: url('data:font/truetype;charset=utf-8;base64,') format('truetype');
}

/* #endif */

.icon-wrapper {
  /* #ifndef APP-NVUE */
  display: flex;
  box-sizing: border-box;
  /* #endif */
  flex-direction: row;
  align-items: center;
  justify-content: center;
}

.iconfont {
  font-family: iconfont !important;
  text-decoration: none;
  text-align: center;
}
</style>
